import styled from 'styled-components';
import {ReactComponent as userAvatar} from '../../assets/images/userAvatar.svg';

export const HeaderContainer = styled.header`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: Arial, Helvetica, sans-serif;
  background-color:${({ theme }) => theme.palette.header};
  /* background-color:${props => props.mobileView === '' ? '#fff' : props.theme.palette.header}; */
  color:${({ theme }) => theme.palette.black};
  font-size: 26px;
  text-align: center;
  line-height: 30px;
  padding: 26px 0;

  @media screen and (min-width: 600px){
    background-color:${props => props.mobileView === '' ? '#fff' : props.theme.palette.header};
  }
`;

export const AvatarIcon = styled(userAvatar)`
    position: absolute;
    right: 0;
    margin-right: 108px;
`;
