import React from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { GoBackButtonWrapper, ArrowLeftIcon, TitleButton } from './styles';

function BackButton() {
  const history = useHistory();
  const locationUrl = useLocation();
  const location = locationUrl.pathname;

  const handleGoBackButton = () => {
    if (location !== '/receiptpage') {
      history.goBack();
    } else {
      history.push('/');
    }
  };

  const showBackButton =
    location === '/checkout' ||
    location === '/receiptpage' ||
    location === '/selectbankpage' ? (
      <GoBackButtonWrapper onClick={handleGoBackButton}>
        <ArrowLeftIcon/>
        <TitleButton>Back</TitleButton>
      </GoBackButtonWrapper>
    ) : (
      <div></div>
    );

  return <div>{showGoBackButton}</div>;
}

export default BackButton;
