import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import PageContainer from "../../components/PageContainer";
import LineStage from "../../components/Stages";
import PaymentMethods from './PaymentMethods';
import Button from '../../components/Buttons/MainButton';
import {
  CheckoutWrapper,
  CheckoutTittle,
  ProductCard,
  Photo,
  Total,
  Delete,
  Amount,
  Description,
  Delivery,
} from './styles';

function Checkout(props) {
  console.log(props)
  return (
    <PageContainer>
      <LineStage />
      <CheckoutWrapper>
        <CheckoutTittle>Checkout</CheckoutTittle>
        <ProductCard>
          <Photo>
            <img src={product.MaxresURL} alt={`Product`} />
          </Photo>

          <Total>
            <p>Total cost</p>
            <p>Delivery included</p>
          </Total>

          <Delete>
            <img
              src={deleteIcon}
              alt="Delete buttom"
              onClick={handleDeleteProduct}
            />
          </Delete>

          <Amount>
            <p>${Math.floor(product.price)}</p>
          </Amount>

          <Description>
            <p>{product.description}</p>
            <p>
              x 1, {product.color}, Size {product.size}
            </p>
            <p>Item #{product.id}</p>
          </Description>

          <Delivery>
            <p>Delivery details</p>
            <p>Jay Nogueira</p>
            <p>Phone no:+5527995792112</p>
            <p>Adress: Guarapari City, 29216-080</p>
          </Delivery>
        </ProductCard>

        <PaymentMethods handlePaymentMethod={handlePaymentMethod} />

        <Button onClick={goToSelectBankPage}>Continue</Button>
      </CheckoutWrapper>
    </PageContainer>
  );
};

export default Checkout;
