import styled from 'styled-components';

export const FilterWrapper = styled.div`
  display: flex;
  border-bottom: 1px solid #bdbdbd;
  width: 100%;
  margin: 0 auto;
  padding-bottom: 0.5rem;
  margin-bottom: 1rem;
`;

export const FilterInput = styled.input`
  width: 100%;
  border: none;
  margin-left: 3.5rem;

  :focus {
    outline: none;
  }
`;
