import styled from 'styled-components';

export const GoBackButtonWrapper = styled.button`
  display: flex;
  align-items: center;
  padding: 0.2rem 0.8rem;
  background: rgba(0, 0, 0, 0.05);
  border: none;
  border-radius: 14px;
  cursor: pointer;

  &:hover,
  :active,
  :focus {
    border: none;
    border-radius: 14px;
    outline: none;
  }

  p {
    font-family: Arial;
    font-size: 18px;
    line-height: 21px;
  }
`;
