import React from 'react';
import PageContainer from "../../components/PageContainer";
import LineStage from "../../components/Stages";
import PaymentMethods from './PaymentMethods';
import Button from '../../components/Buttons/MainButton';
import {
  CheckoutWrapper,
  CheckoutTittle,
  ProductCard,
  Photo,
  Total,
  Delete,
  Amount,
  Description,
  Delivery,
} from './styles';

function Checkout(props) {

  return (
    <PageContainer>
      <LineStage />
      <CheckoutWrapper>
        <CheckoutTittle>Checkout</CheckoutTittle>
        <ProductCard>
          <Photo>
            <img src={props.location.state.MaxresURL} alt={`Product`} />
          </Photo>

          <Total>
            <p>Total cost</p>
            <p>Delivery included</p>
          </Total>

          <Delete>
            <img
              src={deleteIcon}
              alt="Delete buttom"
              onClick={handleDeleteProduct}
            />
          </Delete>

          <Amount>
            <p>${Math.floor(props.location.state.price)}</p>
          </Amount>

          <Description>
            <p>{props.location.state.description}</p>
            <p>
              x 1, {props.location.state.color}, Size {props.location.state.size}
            </p>
            <p>Item #{props.location.state.id}</p>
          </Description>

          <Delivery>
            <p>Delivery details</p>
            <p>Luciano Andrade</p>
            <p>Phone no:+5521974960917</p>
            <p>Adress: Rio de Janeior, 21250-450</p>
          </Delivery>
        </ProductCard>

        <PaymentMethods 
          // handlePaymentMethod={handlePaymentMethod} 
        />

        <Button 
          // onClick={goToSelectBankPage}
        >
            Continue
        </Button>
      </CheckoutWrapper>
    </PageContainer>
  );
};

export default Checkout;
