import styled from 'styled-components';

export const LineStage = styled.div`
    max-width: 859px;
    width: 100%;
    border-top: ${props => `1px solid ${props.theme.palette.borderLineSelect}`};
    display: flex;
    margin: 0 auto;
`;

export const Steps = styled.div`
    max-width: 859px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin: 0 auto;
`;
export const Step = styled.div`
    position: relative;
`;

export const Phase = styled.div`
    height: 15px;
    width: 15px;
    background-color: #61CB46;
    border-radius: 100%;
    margin-bottom: 15px;
`;

export const PhaseTitle = styled.div`
    font-family: Arial;
    font-size: 16px;
    line-height: 18px;
    text-align: center;
    color: ${({theme}) => theme.palette.black};
`;
