import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  margin-top: 47px;
`;

export const ImageProduct = styled.section`
  margin-right: 38px;
  img {
    max-width: 532px;
    width: 100%;
    height: 400px;
    border-radius: 11px;
  }
`;

export const PaymentBox = styled.section`
  max-width: 731px;
  width: 100%;
  height: 400px;
  border-radius: 11px;
  background-color: ${({theme}) => theme.pallete.box}
`;
