import styled from 'styled-components';

export const Select = styled.select`
    border: 1px solid #e8e8e8;
    box-sizing: border-box;
    border-radius: 25px;
    padding: 0.3rem 0.6rem;
    color: ${({ theme }) => theme.palette.alternativeGray};
    &:focus {
      outline: none;
    }
`;
